var ScreenReader = function() {

  var speechSynthesis = window.mozSpeechSynthesis || window.msSpeechSynthesis || window.oSpeechSynthesis || window.webkitSpeechSynthesis || window.speechSynthesis,
    ss, isResult = false;

  var voices = [];

  if (speechSynthesis === undefined) {
    alert('Your browser don\'t support Web Speech Synthesis API. We recommend using chrome browser.');
    return;
  }

  function populateVoiceList() {
    voices = speechSynthesis.getVoices();
  }

  populateVoiceList();

  if (speechSynthesis.onvoiceschanged !== undefined) {
    speechSynthesis.onvoiceschanged = populateVoiceList;
  }

  function start(text, params) {

    var utterThis = new SpeechSynthesisUtterance(text);
    utterThis.pitch = params.pitch || 1;
    utterThis.rate = params.rate || 1;
    utterThis.voice = params.voice || 'English';
    speechSynthesis.speak(utterThis);

    utterThis.onend = function(event) {
      console.log('Finished in ' + event.elapsedTime + ' seconds.');
    }
    utterThis.onpause = function(event) {
      var char = event.utterance.text.charAt(event.charIndex);
      console.log('Speech paused at character ' + event.charIndex + ' of "' +
        event.utterance.text + '", which is "' + char + '".');
    };

  };

  function readIt(text) {
    event.preventDefault();

    // var selectedOption = voiceSelect.selectedOptions[0].getAttribute('data-name');
    var selectedOption = 'Samantha';
    var params = {
      voice: null,
      rate: 1,
      pitch: 1
    };

    for (i = 0; i < voices.length; i++) {
      if (voices[i].name === selectedOption) {
        params.voice = voices[i];
      }
    }

    if (!params.voice) {
      params.voice = voices[1];
    }

    start(text, params);

    return false;
  }

  return {
    read: readIt
  }
}

var accessToken = "a364a593a00349f1ba80c89172914701";
var baseUrl = "https://api.api.ai/v1/";

var recognition;
var SpeechGrammarList;

$(document).ready(function () {
  
  var SPACE_KEY = 32, ENTER_KEY = 13;
  
  $('body').keypress(function (event) {
    if (event.which === SPACE_KEY) {
      event.preventDefault();
      switchRecognition();
    }
  });
  
  // $("#rec").click(function(event) {
  //   switchRecognition();
  // });
  
  $('#rec').on('click', function () {
    send("Help");
  });
});


function startRecognition() {
  recognition = new webkitSpeechRecognition();
  SpeechGrammarList = SpeechGrammarList || webkitSpeechGrammarList;
  
  recognition.onstart = function (event) {
    updateRec();
  };
  
  recognition.onresult = function (event) {
    var text = "";
    for (var i = event.resultIndex; i < event.results.length; ++i) {
      text += event.results[i][0].transcript;
    }
    send(text);
    stopRecognition();
  };
  recognition.onend = function () {
    stopRecognition();
  };
  recognition.lang = "en-US";
  recognition.start();
}

function stopRecognition() {
  if (recognition) {
    recognition.stop();
    recognition = null;
  }
  updateRec();
}

function switchRecognition() {
  if (recognition) {
    stopRecognition();
  } else {
    startRecognition();
  }
}

function updateRec() {
  $("#rec").text(recognition ? "Stop" : "Speak");
}

function performClickOn(content) {
  var el = $(".clickable:contains(" + content + ")");
  var ariaEl = $(".clickable[aria-label*='" + content + "']");
  if (el.length > 0) {
    el.get(0).click();
  } else if (ariaEl.length > 0) {
    ariaEl.get(0).click();
  } else {
    $(":contains(" + content + ")").toArray().forEach(function (el) {
      $(el)[0].click();
    });
  }
}

function send(text) {
  $.ajax({
    type: "POST",
    url: baseUrl + "query?v=20180308",
    contentType: "application/json; charset=utf-8",
    dataType: "json",
    headers: {
      "Authorization": "Bearer " + accessToken
    },
    data: JSON.stringify({
      query: text,
      lang: "en",
      sessionId: "ksksksksks"
    }),
    
    success: function (data) {
      // console.log(data);
      response = data['result']['fulfillment']['speech'];
      if (response == "") {
        response = data['result']['fulfillment']['messages'][1]['speech'];
      }
      
      if (data['result']['action'] == "click-action") {
        performClickOn(response);
      }
    },
    error: function () {
      console.log("Internal server error")
    }
  });
}

